	--color-required: #dc3545;
	--color-clear-button-bg: #6c757d;
	--color-html-text: #555555;
	--color-html-link: #0066cc;
	--color-header-subtext: #777777;

	/* Border Radii */
	--border-radius-card: 8px;
	--border-radius-input: 6px;
	--border-radius-clear-btn: 50%;
