	--opacity-placeholder: 0.6;
}

parameters.jsCode:
// Process and validate the generated Nuclei template
try {
  const items = $input.all();

  return items.map(item => {
    let template = item.json.template;

    // Remove markdown code fences if present
    template = template.replace(/^```yaml\s*/i, '').replace(/```\s*$/, '').trim();

    // Fix common syntax errors
    template = template.replace(/\[\[/g, '{{').replace(/\]\]/g, '}}');  // Fix brackets

    // Extract CVE ID from template
    const cveMatch = template.match(/CVE-\d{4}-\d+/);
    const cveId = cveMatch ? cveMatch[0] : 'nuclei-template';
    const filename = `${cveId}.yaml`;

    // Create binary data for file upload
    const binaryData = Buffer.from(template, 'utf-8');

    return {
      json: {
        cveId: cveId,
        filename: filename,
        message: `Generated Nuclei template for ${cveId}`,
        timestamp: new Date().toISOString()
      },
      binary: {
        data: {
          data: binaryData.toString('base64'),
          mimeType: 'text/yaml',
          fileName: filename,
          fileExtension: 'yaml'
        }
      }
    };
  });
} catch (error) {
  console.error('Template processing error:', error);
  throw new Error(`Failed to process template: ${error.message}`);
}
